#!/usr/bin/env python3

dna = ''

def gc_content(dna): #here I am giving my function a name and a parameter 'dna'    
    c_count = dna.count('C')
    g_count = dna.count("G")
    dna_len = len(dna)
    gc_content = (c_count + g_count) / dna_len
    return gc_content
print(f"G/C ratio : {gc_content(dna)}") # this will give you a decimal (i.e., float)
print(f"G/C percent: {gc_content(dna):.2%}")